import Buttons from './Buttons'
import Display from './Display'


const onlyOneZero = /^(0.[1-9])*\d/g 

function App() {

  const [state, setState] = useState({
    arr: [],
    input: 0
  }) 




 const inputValue = (id) =>{

const previous = state.arr.join("");
const current = id;
