    arr: state.arr.concat(id),
     input: id

   })
   console.log("true")
 } else{ 
   console.log("false")
}
}




 const calculation = () =>{

  let formula = state.arr.join("");
   setState({
     input: eval(formula),
     arr: [...state.arr]

   })

}

 const clearValue = () =>{
   setState({
     arr: [],
     input: 0,
     output: ""
   })
 }


  return (
    <div className="App">
      <div className="display">
          <Display state={state} />
        </div>
        <div className="buttons">
          <Buttons inputValue={inputValue} clearValue={clearValue} calculation={calculation} state={state} />
        </div>

    </div>
  );
}

export default App;
