import { useState } from 'react'
import Buttons from './Buttons'
import Display from './Display'


let onlyOneZero = /^(0{1})[0-9]/;

function App() {

  const [state, setState] = useState({
    arr: [],
    input: 0
  }) 




 const inputValue = (id) =>{

let previous = state.arr.join("");
let current = id;
let prevCur = parseInt(previous.concat(current));

console.log(previous)
console.log(current)

console.log(typeof prevCur)
console.log(prevCur)
console.log(state.arr)


if(onlyOneZero.test(prevCur)){
  setState({
    arr: state.arr.concat(current),
    input: id

   })
   console.log("true")
 } else{ 
   console.log("false")
}
}




 const calculation = () =>{

  let formula = state.arr.join("");
   setState({
     input: eval(formula),
     arr: [...state.arr]

   })

}

 const clearValue = () =>{
   setState({
     arr: [],
     input: 0,
     output: ""
   })
 }


  return (
    <div className="App">
      <div className="display">
          <Display state={state} />
        </div>
        <div className="buttons">
          <Buttons inputValue={inputValue} clearValue={clearValue} calculation={calculation} state={state} />
        </div>

    </div>
  );
}

export default App;
