import { useState } from 'react'
import Buttons from './Buttons'
import Display from './Display'


let onlyOneZero = /^0{1}[1-9]/;

function App() {

  const [state, setState] = useState({
    arr: "",
    input: 0
  }) 




 const inputValue = (id) =>{

let previous = state.arr;
let current = id;
let prevCur = previous+current;
console.log(prevCur)


if(!onlyOneZero.test(prevCur)){
  setState({
    arr: prevCur,
    input: id

   })
   console.log("true")
 } else{ 
   console.log("false")
}
}




 const calculation = () =>{

  let formula = state.arr;
   setState({
     input: eval(formula),
     arr: state.arr

   })

}

 const clearValue = () =>{
   setState({
     arr: [],
     input: 0,
     output: ""
   })
 }


  return (
    <div className="App">
      <div className="display">
          <Display state={state} />
        </div>
        <div className="buttons">
          <Buttons inputValue={inputValue} clearValue={clearValue} calculation={calculation} state={state} />
        </div>

    </div>
  );
}

export default App;
