import { useState } from 'react'
import Buttons from './Buttons'
import Display from './Display'



const startsWithZeros = /^(0+)|([^0.])/
const testString = "0.6"
console.log(startsWithZeros.test(testString))


function App() {

  const [state, setState] = useState({
    initialNumber: 0,
    previousNumber: "",
    currentNumber: "",
    operator: "",
    formula: "",
    fullNumber: "",
    result: ""
  }) 


const inputValue = (i) =>{
  const number = i;
  const formula = state.formula+i

  setState({
    initialNumber: "",
    currentNumber: number,
    formula: formula,
    fullNumber: formula

  })
}


const operator = (symbol) =>{
  setState({
    currentNumber: symbol,
    fullNumber: "",
    formula: state.formula+symbol
    .replace("**", "*")
    .replace("++", "+")
    .replace("--", "-")
    .replace("//", "/")
  })
}

const calculation = () =>{
  console.log(state.formula)
  const result = eval(state.formula)

  setState({

    currentNumber: "= " + state.formula,
    formula: result.toString()


  })  

}


 const clearValue = () =>{
   setState({
     initialNumber: 0,
     previousNumber: "",
     currentNumber: "",
     operator: "",
     formula: ""
   })
 }


  return (
    <div className="App">
      <div className="display">
          <Display state={state} />
        </div>
        <div className="buttons">
          <Buttons clearValue={clearValue} inputValue={inputValue} calculation={calculation} state={state} operator={operator} />
        </div>

    </div>
  );
}

export default App;
