import { useState } from 'react'
import Buttons from './Buttons'
import Display from './Display'


function App() {

  const [state, setState] = useState({
    initialNumber: 0,
    previousNumber: "",
    currentNumber: "",
    operator: "",
    formula: "",
    fullNumber: "",
    result: ""
  }) 


const inputValue = (i) =>{
  const number = i;
  const formula = state.formula+i

  setState({
    initialNumber: "",
    currentNumber: number,
    fullNumber: formula,
    formula: formula
    .replace(/^0*/, "0")
    .replace(/^0[1-9]/, state.currentNumber)
  })
}


const operator = (symbol) =>{
  setState({
    fullNumber: "",
    formula: state.formula+symbol
    .replace("**", "*")
    .replace("++", "+")
    .replace("--", "-")
    .replace("//", "/")
  })
}

const calculation = () =>{
  console.log(state.formula)
  const result = eval(state.formula)

  setState({

    currentNumber: "= " + state.formula,
    formula: result.toString()


  })  

}


 const clearValue = () =>{
   setState({
     initialNumber: 0,
     previousNumber: "",
     currentNumber: "",
     operator: "",
     formula: ""
   })
 }


  return (
    <div className="App">
      <div className="display">
          <Display state={state} />
        </div>
        <div className="buttons">
          <Buttons clearValue={clearValue} inputValue={inputValue} calculation={calculation} state={state} operator={operator} />
        </div>

    </div>
  );
}

export default App;
