import { useState } from 'react'
import Buttons from './Buttons'
import Display from './Display'


function App() {

  const [state, setState] = useState({
    initialNumber: 0,
    previousNumber: "",
    currentNumber: "",
    operator: "",
    formula: "",
    number: 0,
    result: ""
  }) 


const inputValue = (i) =>{
  const number = i;
  const formula = state.formula+i

  setState({
    initialNumber: "",
    currentNumber: number,
    formula: formula
    .replace("..",".")
    .replace("++", "+")
    .replace("--", "-")
    .replace("**", "*")
    .replace("//", "/")
    .replace("/0", "/")
    .replace("*0", "*")
    .replace("-0", "-")
    .replace("+0", "+")
    .replace(/^(0){1,}[1-9]/, "")
  .replace(/^\.\d/,"0."+number)
  })
}


const calculation = () =>{
  console.log(state.formula)
  const result = eval(state.formula)

  setState({

    currentNumber: "= " + state.formula,
    formula: result.toString().replace


  })  

}


 const clearValue = () =>{
   setState({
     initialNumber: 0,
     previousNumber: "",
     currentNumber: "",
     operator: "",
     formula: ""
   })
 }


  return (
    <div className="App">
      <div className="display">
          <Display state={state} />
        </div>
        <div className="buttons">
          <Buttons clearValue={clearValue} inputValue={inputValue} calculation={calculation} state={state} />
        </div>

    </div>
  );
}

export default App;
