import { useState } from 'react'
import Buttons from './Buttons'
import Display from './Display'


function App() {

  const [state, setState] = useState({
    input: 0,
    operator: "",
    formula: ""
  }) 








 const clearValue = () =>{
   setState({
     input: 0,
     operator: "",
     formula: ""
   })
 }


  return (
    <div className="App">
      <div className="display">
          <Display state={state} />
        </div>
        <div className="buttons">
          <Buttons clearValue={clearValue} state={state} />
        </div>

    </div>
  );
}

export default App;
