import { useState } from 'react'
import Buttons from './Buttons'
import Display from './Display'


function App() {

  const [state, setState] = useState({
    initialNumber: 0,
    previousNumber: "",
    currentNumber: "",
    operator: "",
    formula: "",
    number: 0
  }) 


const inputValue = (i) =>{
  const number = i;
  const formula = state.formula+i

  setState({
    initialNumber: "",
    currentNumber: number,
    formula: formula
    .replace("..",".")
    .replace("++", "+")
    .replace("--", "-")
    .replace("**", "*")
    .replace("//", "/")
    .replace("/0", "/")
    .replace("*0", "*")

  })
}




 const clearValue = () =>{
   setState({
     initialNumber: 0,
     previousNumber: "",
     currentNumber: "",
     operator: "",
     formula: ""
   })
 }


  return (
    <div className="App">
      <div className="display">
          <Display state={state} />
        </div>
        <div className="buttons">
          <Buttons clearValue={clearValue} inputValue={inputValue} state={state} />
        </div>

    </div>
  );
}

export default App;
